import React , {useState, useEffect} from 'react';
import './App.css';
import { MenuItem, FormControl, Select, Card, CardContent} from '@material-ui/core';
import InfoBox from './InfoBox';
import Map from './Map';

function App() {
  const [countries, setCountries] = useState([]);
  const [country, setCountry] = useState('worldwide');

  useEffect(() => {
    const getCountriesData = async() => {
      await fetch ("https://disease.sh/v3/covid-19/countries")
      .then((response) => response.json())
      .then((data) => {
        const countries = data.map((country) => (
          {
            name: country.country,
            value: country.countryInfo.iso3
          }
        ));
          setCountries(countries);
      });
    };

    getCountriesData();
  }, []);

  const onCountryChange = (event) => {
    const countryCode = event.target.value;

    setCountry(countryCode);

    //"https://disease.sh/v3/covid-19/all"
    //"https://disease.sh/v3/covid-19/countries/[COUNTRY]"



  }

  return (
    <div className="App">
      <div className="app__left">
        <div className="app__header">
        <h1>Covid-19 Tracker</h1>
          <FormControl className="app__dropdown">
            <Select variant= "outlined" value={country} onChange= {onCountryChange}>

              <MenuItem value="worldwide">Worldwide</MenuItem>
              {countries.map((country)=> (
                <MenuItem value= {country.value}>{country.name}</MenuItem>
              ))}

              {/*
                <MenuItem value = "worldwide">Worldwide</MenuItem>
                <MenuItem value = "worldwide">opt 1</MenuItem>
                <MenuItem value = "worldwide">opt2</MenuItem>
                <MenuItem value = "worldwide">opt3</MenuItem>
              */}
            </Select>
          </FormControl>
      </div>

      <div className="app__stats">
        <InfoBox title="Coronavirus cases" cases={235465} total={23}/>

        <InfoBox title="Recovered" cases= {216} total={234}/>

        <InfoBox title="Deaths " cases= {2135} total= {1222}/>
      </div>

      <Map />

      </div>
      <Card className="app__right">
        <CardContent>
          <h3>Live Cases By Country</h3>

          <h3>Worldwide New Cases</h3>
        </CardContent>
      </Card>
    </div>
  );
}

export default App;
